[Parameter(Mandatory=$true)]
[string] $directory,
[Parameter(Mandatory=$true)]
[string[]] $extensions,
[Parameter(Mandatory=$true)]
[string] $emailRecipient
)

# Define the logging function
function Write-Log {
param (
[string] $Message,
[string] $Path = "$env:TEMP\log.txt",
[switch] $AddTime = $true
)

# Add the timestamp if needed
if ($AddTime) {
$Message = "$(Get-Date -Format "yyyy-MM-dd HH:mm:ss"): $Message"
}

# Write the message to the log file
$Message | Out-File -FilePath $Path -Append
}

# Function to check if a file's extension is in the allowed list
function Test-ValidFileExtension {
param (
[System.IO.FileInfo] $file
)

$valid = $false

foreach ($e in $extensions) {
if ($file.Extension -ieq ".$e") {
$valid = $true
break
}
}

return $valid
}

# Main function
try {
# Get all files in the directory
$files = Get-ChildItem -Path $directory -File

# Loop through each file
foreach ($file in $files) {
# Check if the file has a valid extension
if (Test-ValidFileExtension -file $file) {
# Rename the file
$newName = "New Name" + $file.Extension
Rename-Item -Path $file.FullName -NewName $newName

# Log the renamed file
Write-Log -Message "Renamed file: $($file.FullName) to $newName"

# Send an email with the log file as an attachment
Send-MailMessage `
-To $emailRecipient `
-From "My App<<EMAIL>>" `
-Subject "File Rename Log" `
-Body "The following is the log of renamed files:" `
-BodyAsHtml $true `
-Attachment "$env:TEMP\log.txt"
} else {
# Log the invalid file
Write-Log -Message "Found file with invalid extension: $($file.FullName)"
}
}

# Catch any errors
} catch {
Write-Log -Message "An error occurred: $_" -AddTime
}

